<style lang="sass">

#bol-b.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) forwards

#bol-r.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 0.2s forwards

#bol-h.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 0.4s forwards

#bol-a.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 0.6s forwards

#bol-t.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 0.8s forwards

#bol-tag.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 1s forwards

#bol-reds.animation
	animation: reduceOpacity 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 1.2s forwards, growInSize 1s cubic-bezier(0.205, 0.655, 0.000, 0.995) 1.2s forwards

#bol-reds
	transform-origin: center center

@keyframes reduceOpacity
	0%
		opacity: 0
	100%
		opacity: 1

@keyframes growInSize
	0%
		transform: scale(0.0)
	90%
		transform: scale(1.25)
	100%
		transform: scale(1.0)

#bol-b, #bol-r, #bol-h, #bol-a, #bol-t, #bol-tag
	opacity: 0

#bol-gol-white
	stroke-width: 1px

.rta-row
	position: relative
	width: max-content
	align-items: center
	#tagline
		position: absolute
		bottom: 4px
		right: 24px

#only-motif
	object-fit: cover
	width: 88%
	transform-origin: center center

#onlytextpart
	width: 88%
	margin-top: 16px

.rta-row
	@media screen and (max-width: 1023px)
		height: max-content
		align-items: flex-end
		width: 70%
		#tagline
			width: 36%
			bottom: 24px


</style>