<script lang="ts">

	import { onMount } from 'svelte';
	import { draw } from 'svelte/transition'
	import { elasticIn } from 'svelte/easing'
	import { writable } from 'svelte/store'
	let toggleAnimation = false
	let nice1 = false
	let nice2 = false
	let nice3 = false
	let nice4 = false
	let nice5 = false
	let nice6 = false
	let nice7 = false
	let imageGo = false
	let petalTime = false
	let sunblaze = false
	let lastImage = false

	let audio:HTMLAudioElement
	const currentDuration = writable(0);

	const stateA = writable(false)
	const stateB = writable(false)

	let AValue = false;
	stateA.subscribe(value => {
	AValue = value;
	
	// If A becomes true, set a timeout to set B to true after 10 seconds
	if (AValue) {
	setTimeout(() => {
	stateB.set(true);
	}, 10000);
	}
	});

	$: if ($currentDuration >= 1) {
		toggleAnimation = true
	}

	$: if ($currentDuration >= 16 && $currentDuration <= 18) {
		nice1 = true
	}

	$: if ($currentDuration >= 17 && $currentDuration <= 19) {
		nice2 = true
	}

	$: if ($currentDuration >= 18 && $currentDuration <= 20) {
		nice3 = true
	} 

	$: if ($currentDuration >= 19 && $currentDuration <= 22) {
		nice4 = true
	}

	$: if ($currentDuration >= 21 && $currentDuration <= 24) {
		nice5 = true
	}

	$: if ($currentDuration >= 20 && $currentDuration <= 23) {
		nice6 = true
	} 

	$: if ($currentDuration >= 23 && $currentDuration <= 26) {
		nice7 = true
	} 

	$: if ($currentDuration >= 20) {
		imageGo = true
	}

	$: if ( $currentDuration >= 28){
		petalTime = true
	}

	$: if ( $currentDuration >=30 ){
			sunblaze = true
	}
	
	$: if ($currentDuration >= 31){
		lastImage = true
	}

	function Animate(){
		toggleAnimation = !toggleAnimation
	}

	onMount(() => {
	if (typeof Audio !== 'undefined') {
		audio.addEventListener('timeupdate', () => {
		currentDuration.set(audio.currentTime);
		});
	
		audio.play();
		}
	})


</script>
